INSERT INTO `notices` (`id`, `title`, `content`, `date`, `roles`, `created_at`, `updated_at`) VALUES
(19, 'Video shooting', '<p>All Teacher Aveilelbe for Video Lecture Shooting</p>', '2024-09-10', '[\"4\",\"3\"]', '2024-08-29 15:43:58', '2024-08-29 15:43:58'),
(20, 'School Annual Function 2024', '<p>Invitation to the School Annual Function<br><br>Dear Teachers and Parents,</p><p>We are delighted to invite you to our School Annual Function, which will be held on <strong>September 10, 2024</strong>, at our school auditorium. This event is one of the most anticipated in our school calendar, and we are excited to celebrate the achievements and talents of our students with you.</p><p><strong>Event Details:</strong></p><ul><li><strong>Date:</strong> September 10, 2024</li><li><strong>Time:</strong> 10:00 AM - 2:00 PM</li><li><strong>Venue:</strong> School Auditorium</li></ul><p><strong>Program Highlights:</strong></p><ul><li>Welcome Speech by the Principal</li><li>Cultural Performances by Students</li><li>Award Ceremony for Academic and Extracurricular Achievements</li><li>Annual Report Presentation</li><li>Vote of Thanks</li></ul><p>We request all teachers to ensure their respective classes are prepared and ready for the event. Parents are encouraged to arrive on time and join us in celebrating the hard work and dedication of our students and staff.</p><p>Your presence will greatly encourage our students and make this event a memorable one. We look forward to seeing you all there!</p><p>Thank you for your continued support and cooperation.</p>', '2024-09-11', '[\"4\",\"3\"]', '2024-08-29 15:54:16', '2024-08-29 15:54:16');
